import React from 'react';
import { Container, Row, Col, Tab, Nav } from 'react-bootstrap';
import { ProjectCard } from './ProjectCard';
import projImg1 from '../assets/img/p1.png';
import projImg2 from '../assets/img/p11.png';
import projImg3 from '../assets/img/p2.png';
import projImg4 from '../assets/img/p22.png';
import projImg5 from '../assets/img/p3.png';
import projImg6 from '../assets/img/p33.png';
import colorSharp2 from '../assets/img/color-sharp2.png';
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Projects = () => {
  const projects = [
    {
      title: 'Food Ordering Website',
      description: 'PHP & MYSQL',
      imgUrl: projImg1,
    },
    {
      title: 'Traveller Website',
      description: 'HTML5 & CSS3',
      imgUrl: projImg3,
    },
    {
      title: 'EXPENSE TRACKER',
      description: 'REACT & SPRING BOOT',
      imgUrl: projImg5,
    },
    {
      title: 'Food Ordering Website',
      description: 'PHP & MYSQL',
      imgUrl: projImg2,
    },
    {
      title: 'Traveller Website',
      description: 'HTML5 & CSS3',
      imgUrl: projImg4,
    },
    {
      title: 'EXPENSE TRACKER',
      description: 'REACT & SPRING BOOT',
      imgUrl: projImg6,
    },
  ];

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col xs={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={`animate__animated ${isVisible ? 'animate__fadeIn' : ''}`}>
                  <h2>Projects</h2>
                  <p>
                    Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the
                    industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type
                    and scrambled it to make a type specimen book.
                  </p>
                  <Tab.Container id="projects-tabs" defaultActiveKey="first">
                    <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                      <Nav.Item>
                        <Nav.Link eventKey="first">Tab 1</Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link eventKey="second">Tab 2</Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link eventKey="third">Tab 3</Nav.Link>
                      </Nav.Item>
                    </Nav>
                    <Tab.Content id="slideInUp" className={`animate__animated ${isVisible ? 'animate__slideInUp' : ''}`}>
                      <Tab.Pane eventKey="first">
                        <Row>
                          {projects.map((project, index) => (
                            <Col key={index} md={4}>
                              <ProjectCard {...project} />
                            </Col>
                          ))}
                        </Row>
                      </Tab.Pane>
                      <Tab.Pane eventKey="second">
                        <p>
                          Lorem ipsum dolor sit amet consectetur adipisicing elit. Cumque quam, quod neque provident velit,
                          rem explicabo excepturi id illo molestiae blanditiis, eligendi dicta officiis asperiores delectus
                          quasi inventore debitis quo.
                        </p>
                      </Tab.Pane>
                      <Tab.Pane eventKey="third">
                        <p></p>
                      </Tab.Pane>
                    </Tab.Content>
                  </Tab.Container>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="" />
    </section>
  );
};
