import React, { useState } from 'react';
import emailjs from 'emailjs-com';

const Contacts = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');



  const handleSubmit = (e) => {
    e.preventDefault();

    const serviceId = 'service_88cf1tl'; // Replace with your actual service ID
    const templateId = 'template_ae9kqih'; // Replace with your actual template ID
    const publicKey = 'tFNs-NgyqGytRWEvD'; // Replace with your actual public key

    const templateParams = {
      from_name: name,
      from_email: email,
      to_name: 'web Wizard',
      message: message,
    };

    emailjs.send(serviceId, templateId, templateParams, publicKey)
      .then((response) => {
        console.log('Email sent successfully', response);
        setName('');
        setEmail('');
        setMessage('');
      })
      .catch((error) => {
        console.error('Error sending email', error);
      });
  };

  return (
    <form onSubmit={handleSubmit} className='emailForm'>
      <input
        type='text'
        placeholder='FirstName'
        value={firstname}
        onChange={(e) => setFirstName(e.target.value)}
      />
      <input
        type='text'
        placeholder='LastName'
        value={firstname}
        onChange={(e) => setFirstName(e.target.value)}
      />
      <input
        type='email'
        placeholder='Enter your Email'
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <textarea
        cols="20"
        rows="10"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <button type='submit' color='white'>Send</button>
    </form>
  );
};

export default Contacts;
